package main

import "fmt"

// @lc code=start

func max(i, j int) int {
	if i > j {
		return i
	} else {
		return j
	}

}

func longestValidParentheses2(s string) int {
	count := 0
	stack := []int{}
	stack = append(stack, -1)
	for i := 0; i < len(s)-1; i++ {
		if s[i] == '(' {
			stack = append(stack, i)
		} else {
			stack = stack[:len(stack)-1]
			if len(stack) == 0 {
				stack = append(stack, i)
			} else {
				count = max(count, i-stack[len(stack)-1])
			}
		}
	}
	return count
}

func longestValidParentheses1(s string) int {
	count := 0
	for i := 0; i < len(s); i++ {
		dp := make(map[int]int)
		for j := i; j < len(s); j++ {
			if s[j] == ')' {
				if j > 0 {
					dp[j] = dp[j-1] - 1
				} else {
					dp[j] = -1
				}
			} else {
				if j > 0 {
					dp[j] = dp[j-1] + 1
				} else {
					dp[j] = 1
				}
			}
			if dp[j] == -1 {
				break
			}
			if dp[j] == 0 {
				if j-i+1 > count {
					count = j - i + 1
				}
			}
		}
	}
	return count
}

func longestValidParentheses(s string) int {
	count := 0
	start := -1
	end := 0
	left := 0
	right := 0
	for end < len(s) {
		if s[end] == '(' {
			left++
		} else {
			right++
		}
		if right > left {
			right = 0
			left = 0
			start = end
		} else {
			if right == left && end-start > count {
				count = end - start
			}
		}
		end++
	}
	start = len(s)
	end = len(s) - 1
	left = 0
	right = 0
	for end >= 0 {
		if s[end] == '(' {
			left++
		} else {
			right++
		}
		if left > right {
			right = 0
			left = 0
			start = end
		} else {
			if left == right && start-end > count {
				count = start - end
			}
		}
		end--
	}
	return count
}

